[{"fields": {"modified_date": "2016-03-16T06:15:12.653Z", "modified_by": 1, "admin_note": "", "title": "Page > Base", "custom_template": "{% extends 'base-template' %}

{% load cache staticfiles i18n helpful_tags core_tags cache media_tags %}

{% block title %}{{object.title}}{% endblock %}

{% block content %}

{% block featured_image %}
{% cache CACHE_DURATION 'page-image' object.pk %} 
    {% include 'partials-featured-image' %}
{% endcache %}
{% endblock %}

<div class="bodytext">
    {% block synopsis %}
    {% if object.synopsis %}<h3>{{object.synopsis|safe}}</h3>{% endif %}
    {% endblock %}
    
    {% block object_content %}
    {{object.content|safe}}
    {% endblock %}
    
    {% for block in page_blocks %}
    <section class="page-block {{block.slug}}">
    {{block.content|safe}}
    </section>
    {% endfor %}
</div>

{% endblock %}", "created_by": 1, "version": 1, "created_date": "2016-03-16T06:15:12.653Z", "slug": "page-base", "file_template": null}, "model": "core.template", "pk": 2}]

[{"fields": {"modified_date": "2016-03-16T06:15:42.364Z", "modified_by": 1, "admin_note": "", "title": "Partials > Featured Image", "custom_template": "{% load cache staticfiles i18n helpful_tags core_tags cache media_tags %}
{% if object.image %}
    <figure class="featured">
        {% if variant %}
            {% get_variant_url object.image variant as featured_image_url %}
        {% else %}
            {% get_variant_url object.image 'width_800_fill' as featured_image_url %}
        {% endif %}
        <img src="{{featured_image_url}}" alt="{{object.image.alt}}" itemprop="image" />
        {% if object.image.caption %}<figcaption>{{object.image.caption|safe}}</figcaption>{% endif %}
    </figure>
{% endif %}", "created_by": 1, "version": 1, "created_date": "2016-03-16T06:15:42.364Z", "slug": "partials-featured-image", "file_template": null}, "model": "core.template", "pk": 3}]

