[{"fields": {"modified_date": "2016-03-16T06:30:29.915Z", "modified_by": null, "parent": 1, "admin_note": null, "title": "_Global", "file_source_path": "", "created_by": null, "order": 9, "version": 1, "created_date": "2016-03-16T06:30:29.915Z", "file_source_url": "", "slug": "_global", "custom_source": "body{
    
    color:$dark_grey;
    @include helvetica;
    
    @include triangle-bg;
}
h1, h2, h3, h4, h5, h6{
    @include univers-ultra-condensed;
    letter-spacing:1px;
    text-transform:uppercase;
    margin: 1em 0;
}
h1{
    font-size: 2.2em;
}
h2{
    color:$highlight;
    font-size: 1.8em;
}
h3{
    color:$med_grey;
    font-size: 1.5em;
}
h4{
    font-size: 1.17em;
}
h5{
    color:$highlight;
    font-size: 1.17em;
}
h6{
    @include univers-light-condensed;
    color:$dark_grey;
    font-size: $font_size_base;
}
a{
    @include transition(all 250ms ease-in-out);
    color:$highlight;
    text-decoration: none;
    
    &:visited{
        color:darken($highlight, 10%);
    }
    &:hover, &:active{
        color:$med_grey;
    }
    &.active{
        font-weight:bold;
    }
    
    &.button{
        display:block;
        padding: 1em 1em 0.65em 1em;
        background-color:$neutral;
        color:$brown_grey;
        text-transform:uppercase;
        font-size:$font_size_h4;
        font-weight:300;
        
        &:hover, &:active{
            background-color:$lighter_grey;
        }
    }
}

ul.nav{
    @include univers-light-condensed;
    @include clearfix;
    ul{
        padding:0;
    }
    li{
        float: left;
        list-style-type: none;
        margin: 0 0.5em;
    }
        
    a{
        text-transform: uppercase;
        text-decoration: none;
        color:$dark_grey;
        font-weight: 300;
        &:visited{
            color:$dark_grey;
        }
        &:hover, &:active{
            color:$highlight;
        }
        &.active{
            font-weight:bold;
        }
    }
}
ul, ol{
    li{
        margin-bottom: 0.5em;
    }
}
.inverse{
    background-color:$dark_grey;
    color:$neutral;
    
    a{
        color:$white;
        &:visited{
            color:$white;
        }
        &:hover, &:active{
            color:$highlight;
        }
    }
}

#main{
    & > .container{
        //border: 1px solid transparent;
        
        .inner-container {
            position:relative;
        }
    }
    
    

}

/* helpful tools */
.center-outer {
    display: table;
    position: static;
    width: 100%;
    height: 100%;
}
.center-middle {
    display: table-cell;
    vertical-align: middle;
    width: 100%;
    position: static;
    height: auto !important;
    float: none !important;
}
.center-inner {
    width: 100%;
    height: auto !important;
    float: none !important;
    text-align:center;
    
}
.bodytext{
    figure{
        position:relative;
        
        figcaption{
            width: 100%;
            position:absolute;
            bottom:0;
            padding: 0.5em 1em 0.25em 1em;
            background: #fff;
            background: rgba(255, 255, 255, 0.8);
            font-size:0.8em;
            font-size:0.8rem;
            text-align: left !important;
        }
    }
    ul{
        padding-left:1em;
    }
}
figure.featured{
    margin:0;
    width: calc( 100% + 4em );
    margin-left:-2em;
    figcaption{
        font-size: 0.8em;
        padding: 1em;
    }
    img{
        width:100%;
        display:block;
    }
    
}

.pagination{
    @include clearfix;
    text-align:center;
    @include univers-light-condensed;
    
    li{
        display:inline-block;
        list-style-type: none;
        margin: 0 0.5em;
        
        a{
            display:block;
            padding: 1em 1.2em 0.7em;
            color:$white;
            background: $highlight;
            font-weight:bold;
            
            &:hover, &:active, &.current{
                background:$white;
                color: $highlight;
            }
        }
    }
}", "compiler": "scss"}, "model": "core.cssresource", "pk": 10}]

[{"fields": {"modified_date": "2016-03-16T06:30:29.876Z", "modified_by": null, "parent": 1, "admin_note": null, "title": "_Mixins", "file_source_path": "", "created_by": null, "order": 6, "version": 1, "created_date": "2016-03-16T06:30:29.876Z", "file_source_url": "", "slug": "_mixins", "custom_source": "@mixin clearfix() {
    &:before,
    &:after {
        content: "";
        display: table;
    }
    &:after {
        clear: both;
    }
}
@mixin text-selection{
  ::selection {
    background: $highlight;
    color:$neutral;
  }
  ::-moz-selection {
    background: $highlight;
    color:$neutral;
  }
}
@mixin font-icon(){
  font-family: 'FontAwesome';

  speak: none;
  font-style: normal;
  font-weight: normal;
  font-variant: normal;
  text-transform: none;
  line-height: 1;

  /* Better Font Rendering =========== */
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}
@mixin pointy-hand(){
  cursor: pointer;
  cursor: hand;
}
@mixin opacity($opacity) {
  opacity: $opacity;
  $opacity-ie: $opacity * 100;
  filter: alpha(opacity=$opacity-ie); //IE8
}

@mixin triangle-bg(){
    background-color:$neutral;
    background-image: url(https://ninalp-media.s3.amazonaws.com/media/image/beigepattern-optimized.png);
}
@mixin yellow-dots-bg(){
    background-color:$highlight;
    color:$dark_grey;
    background-image: url(https://ninalp-media.s3.amazonaws.com/media/image/yellowdots.png);
}
@mixin dark-dots-bg(){
    background-color:$dark_grey;
    color:$neutral;
    background-image: url(https://ninalp-media.s3.amazonaws.com/media/image/grey_dots.png);
}
@mixin transparent-dots-bg(){
    background-color:$highlight;
    color:$dark_grey;
    background-image: url(https://ninalp-media.s3.amazonaws.com/media/image/transparent_mesh.png);
}
@mixin demargin-sides(){
    width: calc( 100% + 30px );
    position: relative;
    left: -15px;
}
@mixin demargin-container(){
    padding-top:1em;
    padding-bottom:1em;
    & > *:first-child{
        margin-top:0;
    }
    & > *:last-child{
        margin-bottom:0;
    }
}

@mixin _logo_image($image){
    background-image: url($image);
    background-repeat: no-repeat;
    background-size: 200px 200px;
    background-position: 0 0;
    display: block;
    width: 100px;
    height: 100px;
    padding: 0;
    border: 0;
    margin: 0;
    text-indent: -9999px;
    text-decoration: none;
    float:left;
    transition-duration: 100ms;
    
    &:hover{
        background-position: -100px -100px;
    }
}
@mixin logo-image(){
    @include _logo_image("https://ninalp-media.s3.amazonaws.com/media/image/logo-01.png");
}
@mixin logo-image-bw(){
    @include _logo_image("https://ninalp-media.s3.amazonaws.com/media/image/logo-bw-optimized.png");
    
    background-position: -100px -100px;
    
    &:hover{
        background-position: 0 0;
    }
}

", "compiler": "scss"}, "model": "core.cssresource", "pk": 7}]

