  position:relative;
  &:before{
    content: 'EDIT';
    position:absolute;
    top:0px;
    right:0px;
    font-size: 11px;
    padding: 0.4em 1em 0.2em;
    background: $highlight;
    color:#fff;
    z-index: 1000;
  } 
}
.debug-item{
  &.debug-active{
    border:1px dashed #ccc !important;
    cursor: pointer;
    cursor: hand;

    &:hover{
      border:1px solid $highlight !important;
      @include debug-item-hover();     
    }    
  }
}", "compiler": "scss"}, "model": "core.cssresource", "pk": 11}]

