    background-image: url($image);
    background-repeat: no-repeat;
    background-size: 200px 200px;
    background-position: 0 0;
    display: block;
    width: 100px;
    height: 100px;
    padding: 0;
    border: 0;
    margin: 0;
    text-indent: -9999px;
