[{"fields": {"modified_date": "2016-03-16T06:30:29.926Z", "modified_by": null, "parent": 1, "admin_note": null, "title": "_Debug", "file_source_path": "", "created_by": null, "order": 10, "version": 1, "created_date": "2016-03-16T06:30:29.926Z", "file_source_url": "", "slug": "_debug", "custom_source": ".debug-container{
    padding: 0.5em;
}
@mixin debug-item-hover(){
  position:relative;
  &:before{
    content: 'EDIT';
    position:absolute;
    top:0px;
    right:0px;
    font-size: 11px;
    padding: 0.4em 1em 0.2em;
    background: $highlight;
    color:#fff;
    z-index: 1000;
  } 
}
.debug-item{
  &.debug-active{
    border:1px dashed #ccc !important;
    cursor: pointer;
    cursor: hand;

    &:hover{
      border:1px solid $highlight !important;
      @include debug-item-hover();     
    }    
  }
