            this.footer_height = $(this.options.footerSelector).outerHeight(true);    
    
            if(this.window_width < this.options.fixedHeaderBreakpoint){
                this.remaining_height = Math.max(0, this.window_height - this.header_height - this.footer_height);    
            }else{
                this.remaining_height = Math.max(0, this.window_height - this.footer_height - this.header_height);
