        this._name = pluginName;
        this.debug_active = false;

        this.windows = {};

        this.init();
    }

    DebugItem.prototype = {

        init: function() {
            this.addListeners();
            this.render();
        },
        toggleDebug: function() {
            this.setDebug(!this.debug_active);
        },
        setDebug: function(value) {
            this.debug_active = value;
            this.render()
        },
        selectItem: function(item){
            var url = $(item).attr(this.options.itemURLAttribute);
            
            this.selectionHandler(item, url);
        },
        defaultSelectionHandler: function(item, item_url){
            // window.location = item_url;
            
            if(!item_url in this.windows){
                //Create new window                
                var new_window = this.openWindow(item_url);
            }else{

                //If closed, re-create window
                var win = this.windows[item_url];
                if (win == null || win.closed) {
                    //it was closed, so re-open
                    var new_window = this.openWindow(item_url);

                }else{
                    //still open, just switch focus
                    win.focus();
                }
            }        
            
        },
        openWindow: function(item_url){
            var new_window = window.open('', '');
            this.windows[item_url] = new_window;
            setTimeout(function() {
                new_window.location.href = item_url;
            }, 100);

            return new_window;
        },
        render: function() {
            //Update view

            if(this.debug_active){
                $(this.options.toggleSelector).addClass("active");
                $(this.options.itemSelector).addClass("debug-active");
            }else{
                $(this.options.toggleSelector).removeClass("active");
                $(this.options.itemSelector).removeClass("debug-active");
            }
        },

        addListeners: function() {
            //bind events
            var parent_ref = this;

            $(this.options.toggleSelector).bind("click", function(e){
                e.preventDefault();
                parent_ref.toggleDebug();
            });
            $(this.options.itemSelector).bind("click", function(e){
                if(parent_ref.debug_active){
