 */

;(function ( $, window, document, undefined ) {
    var pluginName = "stickyFooter",
        defaults = {
            headerSelector : "#header",
            mainSelector : "#main",
            footerSelector : "#footer",
            fixedHeaderBreakpoint: 768
        };

    // The actual plugin constructor
    function StickyFooter( element, options ) {
        this.element = element;

        this.options = $.extend( {}, defaults, options) ;

        this.winw = $(window).width(),
        this.winh = $(window).height();

        this._defaults = defaults;
        this._name = pluginName;

        this.init();
    }

    StickyFooter.prototype = {

        init: function() {
            this.updateMeasurements();
            
            this.addListeners();

            this.render()
        },
        
        render: function() {
            //Update view
            $(this.options.mainSelector).css("min-height", this.remaining_height+"px");
            $(this.options.footerSelector).addClass('sticky-footer-inited');
        },
        updateMeasurements: function(){
            this.window_height = $(window).height();
            this.window_width = $(window).width();
            this.header_height = $(this.options.headerSelector).outerHeight(true);
            this.main_height = $(this.options.mainSelector).outerHeight(true);
            this.footer_height = $(this.options.footerSelector).outerHeight(true);    
    
            if(this.window_width < this.options.fixedHeaderBreakpoint){
                this.remaining_height = Math.max(0, this.window_height - this.header_height - this.footer_height);    
            }else{
                this.remaining_height = Math.max(0, this.window_height - this.footer_height - this.header_height);
            }
            
        },
        addListeners: function() {
            //bind events
            var parent = this;
            $(window).bind("resize", function(){
                if( $(window).width()!= parent.winw || $(window).height()!= parent.winh ){
                    parent.winw = $(window).width(),
                    parent.winh = $(window).height();
                    parent.updateMeasurements();
                    parent.render();
                }                
            }); 
            $(window).bind("load", function() { 
                parent.updateMeasurements();
                parent.render();
            });
        },

        removeListeners: function() {
            //unbind events    
            var parent = this;      
            $(window).unbind("resize", function(){
                parent.updateMeasurements();
                parent.render();
            });  
            $(window).unbind("load", function() { 
                parent.updateMeasurements();
                parent.render();
            });
        }

    };

    // A really lightweight plugin wrapper around the constructor,
    // preventing against multiple instantiations
    $.fn[pluginName] = function ( options ) {
        return this.each(function () {
            if (!$.data(this, "plugin_" + pluginName)) {
                $.data(this, "plugin_" + pluginName,
                new StickyFooter( this, options ));
            }
        });
    };

