 * author: nina@ninalp.com
 * A class to encapsulate common link management
 */


;(function ( $, window, document, undefined ) {

    

    // Create the defaults once
    var pluginName = "linkManager",
        defaults = {
            activeClass: "active",
            parentActiveClass : "parent-of-active",
            externalClass : "external",
            mailtoClass : "mailto",
            openLinksInNewWindow : true,
            scrollToAnchors: true
        };

    // The actual plugin constructor
    function LinkManager( element, options ) {
        this.element = element;

        
        this.options = $.extend( {}, defaults, options) ;

        this._defaults = defaults;
        this._name = pluginName;


        this.init();
    }

    LinkManager.prototype = {

        init: function() {
            
            var parent = this;
            $(this.element).find( 'a' ).each(function() {
                    
                var active = parent.isLinkActive(this);
                var parent_active = parent.isParentActive(this);
                var external = parent.isExternal(this);
                var is_anchor = parent.isInternalAnchor(this);
                var mailto = parent.isMailto(this);

                //console.log(this+" external? "+external)

                if(active){ 
                    $(this).addClass(parent.options.activeClass);
                }
                if(parent_active){ 
                    $(this).addClass(parent.options.parentActiveClass);
                }

                if(parent.options.openLinksInNewWindow == true && external==true && mailto == false){
                    $(this).addClass(parent.options.externalClass);
                    $(this).attr("target", "_blank");
                }
                if(mailto == true){
                    $(this).addClass(parent.options.mailtoClass);   
                }    

                if(parent.options.scrollToAnchors == true && is_anchor==true){
                
                    $(this).bind("mousedown", function(event){
                        parent.anchorClicked(event);
                    });
                }
                
            });
                

        },
        
        anchorClicked:function(event){
            var current_hash = window.location.hash;
            var href = $(this).attr("href");
            if(typeof(href)=='undefined'){
                return;
            }
            var hash = href.substring(href.indexOf('#'));
            if(hash && current_hash!=hash){

                var offset_y = not_empty($(this).data('scroll-offset')) ? $(this).data('scroll-offset') : 0;

                event.preventDefault(); 
                var dest = 0;
                if ($(this.hash).offset().top > $(document).height() - $(window).height()) {
                    dest = $(document).height() - $(window).height() - offset_y;
                } else {
                    dest = $(this.hash).offset().top - offset_y;
                }
                //go to destination
                $('html,body').animate({
                    scrollTop: dest
                }, 500, 'swing');
            }
        },
        isLinkActive:function(link){

            var link_path = link.pathname;
            var href = $(link).attr("href");
            if(!link_path || link_path == "" || link_path == "/" || !href || href == "" || href == "#"){
                return false;
            }

            if(link_path.charAt(0)!="/"){
                //has no starting slash. add one for consistency
                link_path = "/"+link_path
            }

            var window_path = window.location.pathname;
            
            if (link_path == window_path) {
                return true;
            }
            
            return false;
        },

        isParentActive: function(link){

            //if url is domain.com/group/item
            //then domain.com/group or domain.com/group/ is active parent
            var link_path = link.pathname;
            var href = $(link).attr("href");
                if(!link_path || link_path == "" || link_path == "/" || !href || href == "" || href == "#"){
                return false;
            }

            var window_path = window.location.pathname;
            var are_different = link_path != window_path;
            var match_index = window_path.indexOf(link_path)>=0;

            if (are_different && match_index) {
                return true;
            }

            return false;
        },
        isExternal: function(link) {

            var url = $(link).attr("href");

            if( typeof(url) == 'undefined'){
                return false;
            }
            
            //test if current domain in url
            var current_domain = window.location.host; //window.location.protocol+"//"+
            if(url.indexOf(current_domain) >= 0){
                return false;
            }

            var match = url.match(/^([^:\/?#]+:)?(?:\/\/([^\/?#]*))?([^?#]+)?(\?[^#]*)?(#.*)?/);
            if (typeof match[1] === "string" && match[1].length > 0 && match[1].toLowerCase() !== location.protocol) return true;
            if (typeof match[2] === "string" && match[2].length > 0 && match[2].replace(new RegExp(":("+{"http:":80,"https:":443}[location.protocol]+")?$"), "") !== location.host) return true;
            return false;
        },
        isInternalAnchor: function(link){
            var url = $(link).attr("href");
            if( typeof(url) == 'undefined'){
                return false;
            }

            return this.isExternal(link) == false && url.indexOf('#') >= 0;
        },
        isMailto: function(link){
            var url = $(link).attr("href");
            if( typeof(url) == 'undefined'){
                return false;
            }

            return url.toLowerCase().indexOf('mailto') >= 0;
        },
        containsString: function(link, string){

            //if url is domain.com/group/item
            //then domain.com/group or domain.com/group/ is active parent
            var link_path = link.pathname;
            var href = $(link).attr("href");
                if(!link_path || link_path == "" || link_path == "/" || !href || href == "" || href == "#"){
                return false;
            }

            return link_path.indexOf(string)>=0;
        }

    };

    $.fn[pluginName] = function ( options ) {
        return this.each(function () {
            if (!$.data(this, "plugin_" + pluginName)) {
                $.data(this, "plugin_" + pluginName,
                new LinkManager( this, options ));
            }
        });
