<!--[if gt IE 9]>    <html class="no-js gte-ie9" lang="en"> <![endif]-->
<!--[if !IE ]>    <html class="no-js gte-ie9" lang="en"> <![endif]-->
<html class="no-js gte-ie9" lang="en" >
    <head>
        <meta charset="utf-8" />
        <meta http-equiv="X-UA-Compatible" content="IE=edge" />
        <meta name="viewport" content="width=device-width, initial-scale=1" />

        <title>{% block title %}{% if object %}{{ object.title }}{% endif %}{% endblock %}{% block titlediv %} | {% endblock %}{{ SITE_TITLE }}</title>
        <meta name="description" content="{% block description %}{% if object.page_meta_description %}{{object.page_meta_description}}{% else %}Software Developer{% endif %}{% endblock %}" />
        <meta name="keywords" content="{% block keywords %}{% if object.page_meta_keywords %}{{object.page_meta_keywords}}{% else %}Web development, open source, software{% endif %}{% endblock %}" />

        <!-- Facebook Open Graph -->
        <meta property="og:title" content="{% block og:title %}{{ object.title }} | {{ SITE_TITLE }}{% endblock %}" />
        <meta property="og:description" content="{% block og:description %}{% if object.page_meta_description %}{{object.page_meta_description}}{% elif object.get_synopsis %}{{object.get_synopsis}}{% else %}Software Developer{% endif %}{% endblock %}" />
        <meta property="og:type" content="{% block og:type %}article{% endblock %}" />
        <meta property="og:url" content="{{ request.build_absolute_uri }}" />
        <meta property="og:site_name" content="{{ SITE_TITLE }}" />
        <meta property="og:image" content="{% block og:image %}{% if object and object.image %}{% get_variant_url object.image 'width_800_fill' as image_small %}{{image_small}}{% else %}{% static 'social-icon.png' %}{% endif %}{% endblock %}" />

        <!-- Twitter Card -->
        <meta name="twitter:card" content="summary" />
        <meta name="twitter:site" content="{% block twitter:site %}@lolablissie{% endblock %}" />
        <meta name="twitter:title" content="{% block twitter:title %}{{ object.title }} | {{ SITE_TITLE }}{% endblock %}" />
        <meta name="twitter:description" content="{% block twitter:description %}{% if object.page_meta_description %}{{object.page_meta_description}}{% elif object.get_synopsis %}{{object.get_synopsis}}{% else %}Software Developer{% endif %}{% endblock %}" />
        <meta name="twitter:url" content="{{ request.build_absolute_uri }}" />
        <meta name="twitter:image:src" content="{% block twitter:image %}{% if object and object.image %}{% get_variant_url object.image 'width_800_fill' as image_small %}{{image_small}}{% else %}{% static 'social-icon.png' %}{% endif %}{% endblock %}" />
        <meta name="twitter:creator" content="{% block twitter:creator %}@lolablissie{% endblock %}" />
        
        <!-- Schema.org markup for Google+ --> 
        <meta itemprop="name" content="{% block google:title %}{{ object.title }} | {{ SITE_TITLE }}{% endblock %}" />
        <meta itemprop="description" content="{% block google:description %}{% if object.page_meta_description %}{{object.page_meta_description}}{% elif object.get_synopsis %}{{object.get_synopsis}}{% else %}Software Developer{% endif %}{% endblock %}" />
        <meta itemprop="image" content="{% block google:image %}{% if object and object.image %}{% get_variant_url object.image 'width_800_fill' as image_small %}{{image_small}}{% else %}{% static 'social-icon.png' %}{% endif %}{% endblock %}" />

        <link rel="canonical" href="{% block canonical_url %}{% get_canonical_url as canonical_url %}{{canonical_url}}{% endblock %}" />

        {% include 'partials-favicons' %}

        <!-- WEBMASTER VERIFICATION -->
        <meta name="google-site-verification" content="NvxKH6UoD7cWFKpQ8zn9iB4oaqJoDd8Ac9IOK3e0DZg" />
        <meta name="msvalidate.01" content="F16D938755E76715C40CD61FBF24BEE3" />
        
        
        {% comment %}
        {% if DEBUG %}
        {% get_css_package 'discipline-report-core' False as css_package %}
        <link rel="stylesheet" type="text/css" media="all" href="{{css_package}}"/>
        {% else %}
        {% cache CACHE_DURATION 'css-package' %} 
        {% get_css_package 'discipline-report-core' True as css_package %}
        <link rel="stylesheet" type="text/css" media="all" href="{{css_package}}"/>
        {% endcache %}
        {% endif %}
        {% endcomment %}
        
        <!-- Core Site Styles -->
        <!-- this is minifed -->
        <style> 
        {% if DEBUG %}
            {% get_css_source 'discipline-report-core' True as css_source %}
            {{css_source|safe}}
        {% else %}
            {% cache CACHE_DURATION 'discipline-report-core-css-source' %} 
            {% get_css_source 'discipline-report-core' True as css_source %}
            {{css_source|safe}}
            {% endcache %}
        {% endif %}
        </style> 

        {% block css %}{% endblock %}     
        
        
        <!--[if lt IE 9]>
            
            <script src="//cdnjs.cloudflare.com/ajax/libs/html5shiv/3.6.2/html5shiv.js"></script>

            <script src="{% static 'disciplinereport/js/vendor/respond.min.js'%}"></script>
            <link href="{% static 'disciplinereport/js/vendor/respond-proxy.html'%}" id="respond-proxy" rel="respond-proxy" />

            <link href="/disciplinereport/js/vendor/respond.proxy.gif" id="respond-redirect" rel="respond-redirect" />
            <script src="/disciplinereport/js/vendor/respond.proxy.js"></script>        
        
            <script src="//s3.amazonaws.com/nwapi/nwmatcher/nwmatcher-1.2.5-min.js"></script>
            <script src="//html5base.googlecode.com/svn-history/r38/trunk/js/selectivizr-1.0.3b.js"></script>
            
            
        <![endif]-->

        {% include 'partials-modernizr' %}
        
        
    </head>
    <body class="{% block bodyclass %}{% endblock %} {% if object %}page-{{object.slug}}{% endif %}" data-site-id="{{SITE_ID}}" {% block bodydata %}{% endblock %} {% block bodyscope %}itemscope itemType="https://schema.org/WebPage"{% endblock %}>
        
        {% block page_header_container %}
        {% include 'partials-header' %}
        {% endblock %}
        
        {% block below_header %}{% endblock %}

        {% block main %}
        <div id="main"  >
            <div class="container">
                <div class="inner-container">
                    
                
                {% block breadcrumbs %}
                {% with hierarchy=object.get_hierarchy %}
                {% if hierarchy|length > 2 %}
                    <ol class="breadcrumb">
                    {% for page in hierarchy %}
                        {% if forloop.first %}
                        
                        {% elif forloop.last %}
                        <li {% if forloop.last %}class="last"{% endif %}>{{page.title}}</li>
                        {% else %}
                        <li {% if forloop.last %}class="last"{% endif %}><a href="{{page.get_absolute_url}}">{{page.title}}</a></li>
                        {% endif %}
                    {% endfor %}
                    </ol>
                {% endif %}
                {% endwith %}
                {% endblock %}
                
                {% if messages %}
                <div class="messages">
                {% block messages %}
                    <ul>
                        {% for message in messages %}
                        <li{% if message.tags %} class="{{ message.tags }}"{% endif %}>{{ message }}</li>
                        {% endfor %}
                    </ul>
                {% endblock %}
                </div>
                {% endif %}
                
                {% block pagetitle %}{% if object %}<h1 itemprop="name">{{ object.title }}</h1>{% endif %}{% endblock %}
                
                {% block content %}{% endblock %}
                
                </div>
            </div>               
        </div>
        {% endblock %}
    
        {% block page_footer_container %}
        {% include 'partials-footer' %}
        {% endblock %}
        
        
        <!-- Jquery other libs, and custom jQuery -->
        {% if DEBUG %}
        {% get_js_package 'discipline-report-core' False as js_package %}
        <script src="{{js_package}}" ></script>
        {% else %}
        {% cache CACHE_DURATION 'js-package' %} 
        {% get_js_package 'discipline-report-core' True as js_package %}
        <script src="{{js_package}}" ></script>
        {% endcache %}
        {% endif %}
        
        <!-- Comes after disciplinereport.core.min.js because it requires jquery -->
        <!--[if lte IE 9]>
            <script src="{% static 'disciplinereport/js/vendor/jquery.placeholder.min.js' %}" ></script>
        <![endif]-->
        
        {% include 'partials-analytics' %}
        
        {% block js %}{% endblock %}

    </body>
</html>
