[{"fields": {"modified_date": "2016-03-16T06:45:11.675Z", "modified_by": 1, "admin_note": "", "title": "Partials > Footer", "custom_template": "{% load staticfiles i18n helpful_tags core_tags cache %}
<footer id="footer">
    <div class="container" itemscope itemtype="http://schema.org/LocalBusiness">
        
        {% cache CACHE_DURATION 'footer' %} 
        <div class="footer-links">
            <p>Developed by Nina Pavlich, students from Strive, and Padres Unidos.</p>
            
            
            {% get_link_descendants "footer-menu" as footerlinks %}
            <ul class="nav debug-item {{footerlinks.css_classes}}" {{footerlinks.item|edit_attribute}} >
                {% for obj in footerlinks.children %}
                <li class="{{obj.item.css_classes}}"><a href="{{ obj.item.path }}"><span {{obj.item.extra_attributes|safe}}>{{ obj.item.title }}</span></a></li>
                {% endfor %}
            </ul>
        </div>
        
        <div class="footer-social-links">
            <h6>Connect With Us</h6>
            {% get_link_descendants "social-media-links" as sociallinks %}
            <ul class="nav debug-item  {{footerlinks.css_classes}}" {{sociallinks.item|edit_attribute}} >
                {% for obj in sociallinks.children %}
            	<li class="{{obj.item.css_classes}}"><a href="{{ obj.item.path }}" target="_blank"  {{obj.item.extra_attributes|safe}} ><i class="fa fa-{{obj.item.slug}}"></i></a></li>
                {% endfor %}
            </ul>
        </div>
        {% endcache %}
        
    </div>
</footer>", "created_by": 1, "version": 2, "created_date": "2016-03-16T06:17:28.745Z", "slug": "partials-footer", "file_template": null}, "model": "core.template", "pk": 6}]

