fields.custom_template:
{% load staticfiles i18n core_tags helpful_tags cache media_tags %}
<!DOCTYPE html>
<!--[if lt IE 7]> <html class="no-js ie lt-ie7 lt-ie9" lang="en"> <![endif]-->
<!--[if IE 7]>    <html class="no-js ie ie7 lt-ie9" lang="en"> <![endif]-->
<!--[if IE 8]>    <html class="no-js ie ie8 lt-ie9" lang="en"> <![endif]-->
<!--[if IE 9]>    <html class="no-js ie ie9 gte-ie9" lang="en"> <![endif]-->
<!--[if gt IE 9]>    <html class="no-js gte-ie9" lang="en"> <![endif]-->
<!--[if !IE ]>    <html class="no-js gte-ie9" lang="en"> <![endif]-->
<html class="no-js gte-ie9" lang="en" >
    <head>
        <meta charset="utf-8" />
        <meta http-equiv="X-UA-Compatible" content="IE=edge" />
        <meta name="viewport" content="width=device-width, initial-scale=1" />

        <title>{% block title %}{% if object %}{{ object.title }}{% endif %}{% endblock %}{% block titlediv %} | {% endblock %}{{ SITE_TITLE }}</title>
        <meta name="description" content="{% block description %}{% if object.page_meta_description %}{{object.page_meta_description}}{% else %}Software Developer{% endif %}{% endblock %}" />
        <meta name="keywords" content="{% block keywords %}{% if object.page_meta_keywords %}{{object.page_meta_keywords}}{% else %}Web development, open source, software{% endif %}{% endblock %}" />

        <!-- Facebook Open Graph -->
        <meta property="og:title" content="{% block og:title %}{{ object.title }} | {{ SITE_TITLE }}{% endblock %}" />
        <meta property="og:description" content="{% block og:description %}{% if object.page_meta_description %}{{object.page_meta_description}}{% elif object.get_synopsis %}{{object.get_synopsis}}{% else %}Software Developer{% endif %}{% endblock %}" />
        <meta property="og:type" content="{% block og:type %}article{% endblock %}" />
        <meta property="og:url" content="{{ request.build_absolute_uri }}" />
        <meta property="og:site_name" content="{{ SITE_TITLE }}" />
        <meta property="og:image" content="{% block og:image %}{% if object and object.image %}{% get_variant_url object.image 'width_800_fill' as image_small %}{{image_small}}{% else %}{% static 'social-icon.png' %}{% endif %}{% endblock %}" />

        <!-- Twitter Card -->
        <meta name="twitter:card" content="summary" />
        <meta name="twitter:site" content="{% block twitter:site %}@lolablissie{% endblock %}" />
        <meta name="twitter:title" content="{% block twitter:title %}{{ object.title }} | {{ SITE_TITLE }}{% endblock %}" />
        <meta name="twitter:description" content="{% block twitter:description %}{% if object.page_meta_description %}{{object.page_meta_description}}{% elif object.get_synopsis %}{{object.get_synopsis}}{% else %}Software Developer{% endif %}{% endblock %}" />
        <meta name="twitter:url" content="{{ request.build_absolute_uri }}" />
        <meta name="twitter:image:src" content="{% block twitter:image %}{% if object and object.image %}{% get_variant_url object.image 'width_800_fill' as image_small %}{{image_small}}{% else %}{% static 'social-icon.png' %}{% endif %}{% endblock %}" />
        <meta name="twitter:creator" content="{% block twitter:creator %}@lolablissie{% endblock %}" />
        
        <!-- Schema.org markup for Google+ --> 
        <meta itemprop="name" content="{% block google:title %}{{ object.title }} | {{ SITE_TITLE }}{% endblock %}" />
        <meta itemprop="description" content="{% block google:description %}{% if object.page_meta_description %}{{object.page_meta_description}}{% elif object.get_synopsis %}{{object.get_synopsis}}{% else %}Software Developer{% endif %}{% endblock %}" />
        <meta itemprop="image" content="{% block google:image %}{% if object and object.image %}{% get_variant_url object.image 'width_800_fill' as image_small %}{{image_small}}{% else %}{% static 'social-icon.png' %}{% endif %}{% endblock %}" />

        <link rel="canonical" href="{% block canonical_url %}{% get_canonical_url as canonical_url %}{{canonical_url}}{% endblock %}" />

        {% include 'partials-favicons' %}

        <!-- WEBMASTER VERIFICATION -->
        <meta name="google-site-verification" content="NvxKH6UoD7cWFKpQ8zn9iB4oaqJoDd8Ac9IOK3e0DZg" />
        <meta name="msvalidate.01" content="F16D938755E76715C40CD61FBF24BEE3" />
        
        
        {% comment %}
        {% if DEBUG %}
        {% get_css_package 'discipline-report-core' False as css_package %}
        <link rel="stylesheet" type="text/css" media="all" href="{{css_package}}"/>
        {% else %}
        {% cache CACHE_DURATION 'css-package' %} 
        {% get_css_package 'discipline-report-core' True as css_package %}
        <link rel="stylesheet" type="text/css" media="all" href="{{css_package}}"/>
        {% endcache %}
        {% endif %}
        {% endcomment %}
        
        <!-- Core Site Styles -->
        <!-- this is minifed -->
        <style> 
        {% if DEBUG %}
            {% get_css_source 'discipline-report-core' True as css_source %}
            {{css_source|safe}}
        {% else %}
            {% cache CACHE_DURATION 'discipline-report-core-css-source' %} 
            {% get_css_source 'discipline-report-core' True as css_source %}
            {{css_source|safe}}
            {% endcache %}
        {% endif %}
        </style> 

        {% block css %}{% endblock %}     
        
        
        <!--[if lt IE 9]>
            
            <script src="//cdnjs.cloudflare.com/ajax/libs/html5shiv/3.6.2/html5shiv.js"></script>

            <script src="{% static 'disciplinereport/js/vendor/respond.min.js'%}"></script>
            <link href="{% static 'disciplinereport/js/vendor/respond-proxy.html'%}" id="respond-proxy" rel="respond-proxy" />

            <link href="/disciplinereport/js/vendor/respond.proxy.gif" id="respond-redirect" rel="respond-redirect" />
            <script src="/disciplinereport/js/vendor/respond.proxy.js"></script>        
        
            <script src="//s3.amazonaws.com/nwapi/nwmatcher/nwmatcher-1.2.5-min.js"></script>
            <script src="//html5base.googlecode.com/svn-history/r38/trunk/js/selectivizr-1.0.3b.js"></script>
            
            
        <![endif]-->

        {% include 'partials-modernizr' %}
        
        
    </head>
    <body class="{% block bodyclass %}{% endblock %} {% if object %}page-{{object.slug}}{% endif %}" data-site-id="{{SITE_ID}}" {% block bodydata %}{% endblock %} {% block bodyscope %}itemscope itemType="https://schema.org/WebPage"{% endblock %}>
        
        {% block page_header_container %}
        {% include 'partials-header' %}
        {% endblock %}
        
        {% block below_header %}{% endblock %}

        {% block main %}
        <div id="main"  >
            <div class="container">
                <div class="inner-container">
                    
                
                {% block breadcrumbs %}
                {% with hierarchy=object.get_hierarchy %}
                {% if hierarchy|length > 2 %}
                    <ol class="breadcrumb">
                    {% for page in hierarchy %}
                        {% if forloop.first %}
                        
                        {% elif forloop.last %}
                        <li {% if forloop.last %}class="last"{% endif %}>{{page.title}}</li>
                        {% else %}
                        <li {% if forloop.last %}class="last"{% endif %}><a href="{{page.get_absolute_url}}">{{page.title}}</a></li>
                        {% endif %}
                    {% endfor %}
                    </ol>
                {% endif %}
                {% endwith %}
                {% endblock %}
                
                {% if messages %}
                <div class="messages">
                {% block messages %}
                    <ul>
                        {% for message in messages %}
                        <li{% if message.tags %} class="{{ message.tags }}"{% endif %}>{{ message }}</li>
                        {% endfor %}
                    </ul>
                {% endblock %}
                </div>
                {% endif %}
                
                {% block pagetitle %}{% if object %}<h1 itemprop="name">{{ object.title }}</h1>{% endif %}{% endblock %}
                
                {% block content %}{% endblock %}
                
                </div>
            </div>               
        </div>
        {% endblock %}
    
        {% block page_footer_container %}
        {% include 'partials-footer' %}
        {% endblock %}
        
        
        <!-- Jquery other libs, and custom jQuery -->
        {% if DEBUG %}
        {% get_js_package 'discipline-report-core' False as js_package %}
        <script src="{{js_package}}" ></script>
        {% else %}
        {% cache CACHE_DURATION 'js-package' %} 
        {% get_js_package 'discipline-report-core' True as js_package %}
        <script src="{{js_package}}" ></script>
        {% endcache %}
        {% endif %}
        
        <!-- Comes after disciplinereport.core.min.js because it requires jquery -->
        <!--[if lte IE 9]>
            <script src="{% static 'disciplinereport/js/vendor/jquery.placeholder.min.js' %}" ></script>
        <![endif]-->
        
        {% include 'partials-analytics' %}
        
        {% block js %}{% endblock %}

    </body>
</html>

{% extends 'base-template' %}

{% load cache staticfiles i18n helpful_tags core_tags cache media_tags %}

{% block title %}{{object.title}}{% endblock %}

{% block content %}

{% block featured_image %}
{% cache CACHE_DURATION 'page-image' object.pk %} 
    {% include 'partials-featured-image' %}
{% endcache %}
{% endblock %}

<div class="bodytext">
    {% block synopsis %}
    {% if object.synopsis %}<h3>{{object.synopsis|safe}}</h3>{% endif %}
    {% endblock %}
    
    {% block object_content %}
    {{object.content|safe}}
    {% endblock %}
    
    {% for block in page_blocks %}
    <section class="page-block {{block.slug}}">
    {{block.content|safe}}
    </section>
    {% endfor %}
</div>

{% endblock %}

{% load cache staticfiles i18n helpful_tags core_tags cache media_tags %}
{% if object.image %}
    <figure class="featured">
        {% if variant %}
            {% get_variant_url object.image variant as featured_image_url %}
        {% else %}
            {% get_variant_url object.image 'width_800_fill' as featured_image_url %}
        {% endif %}
        <img src="{{featured_image_url}}" alt="{{object.image.alt}}" itemprop="image" />
        {% if object.image.caption %}<figcaption>{{object.image.caption|safe}}</figcaption>{% endif %}
    </figure>
{% endif %}

{% load staticfiles i18n helpful_tags core_tags cache %}

{% block page_header %}
<header id="header">            
    
<nav class="navbar navbar-fixed-top">
    
    {% include 'partials-debug' %}
    
    
    {% block page_header_inner %}
    <div class="container menu">
        
        <div>
            <div id="navbar">
           
                <a class="navbar-brand" href="/">
                  {% block site_header_title %}
                  <span>Home</span>
                  {% endblock %}
                </a>
                
                {% cache CACHE_DURATION 'header-menu' %} 
                
                {% get_link_descendants "header-menu" as headerlinks %}
                <ul class="nav navbar-nav debug-item {{headerlinks.item.css_classes}}" {{headerlinks.item|edit_attribute}}>
                
                {% for obj in headerlinks.children %}
                
                {% if obj.children.children|length > 0 %}
                <li class="dropdown">
                    <a href="{{ obj.item.path }}" class="dropdown-toggle" data-toggle="dropdown" role="button" aria-expanded="false" >{{ obj.item.title }} <span class="caret"></span></a>
                    
                    <ul class="dropdown-menu" role="menu">
                    {% for child in obj.children.children %}
                        <li class="level-1"><a href="{{ child.item.path }}">{{ child.item.title }}</a></li>
                    
                        {% for subchild in child.children.children %}
                        <li class="level-2"><a href="{{ subchild.item.path }}">{{ subchild.item.title }}</a></li>
                        {% endfor %}
                        
                    {% endfor %}
                    </ul>
                </li>
                {% else %}
                <li><a href="{{ obj.item.path }}">{{ obj.item.title }}</a></li>
                {% endif %}
                
                {% endfor %}
                
                </ul>
                {% endcache %}
                
                
                
            </div>
        </div>
    </div>
    {% endblock %}
    
</nav>          
</header>
{% endblock %}

{% load staticfiles i18n helpful_tags core_tags cache %}
<footer id="footer">
    <div class="container" itemscope itemtype="http://schema.org/LocalBusiness">
        
        {% cache CACHE_DURATION 'footer' %} 
        <div class="footer-links">
            <p>Developed by....</p>
            
            
            {% get_link_descendants "footer-menu" as footerlinks %}
            <ul class="nav debug-item {{footerlinks.css_classes}}" {{footerlinks.item|edit_attribute}} >
                {% for obj in footerlinks.children %}
                <li class="{{obj.item.css_classes}}"><a href="{{ obj.item.path }}"><span {{obj.item.extra_attributes|safe}}>{{ obj.item.title }}</span></a></li>
                {% endfor %}
            </ul>
        </div>
        
        <div class="footer-social-links">
            <h6>Connect With Us</h6>
            {% get_link_descendants "social-media-links" as sociallinks %}
            <ul class="nav debug-item  {{footerlinks.css_classes}}" {{sociallinks.item|edit_attribute}} >
                {% for obj in sociallinks.children %}
            	<li class="{{obj.item.css_classes}}"><a href="{{ obj.item.path }}" target="_blank"  {{obj.item.extra_attributes|safe}} ><i class="fa fa-{{obj.item.slug}}"></i></a></li>
                {% endfor %}
            </ul>
        </div>
        {% endcache %}
        
    </div>
</footer>

{% load i18n helpful_tags %}

{% if request.user or request.original_user %}
{% if request.user.is_staff or request.original_user.is_staff %}
<div class="debug-container inverse">
    <div class="container">
            
        &raquo; 
        
        {% if object %}
    	{% get_model_label object as model_label %}
        <span>{{model_label}} [{{object.pk}}]</span>        
        
        {% if object.is_published == False %}<strong>This item is not published</strong>{% endif %}
        {% if object.is_moderated == False %}<strong>This item is not moderated</strong>{% endif %}
        
        
        {% url_to_edit_object object as edit_url %}
        <a href="{{edit_url}}" target="_blank">{% trans "Edit in admin" %}</a>  
        {% endif %}
         
        
        <span class="pull-right">
            {% if request.impersonating %}Impersonating {{request.impersonating}} | <a href='{% url_add_query __unimpersonate=True %}'>Stop Impersonating</a>{% endif %}
            <a href="#" class="debug-item-toggle"><i class="fa fa-pencil"></i></a>
        </span>
        
    </div>
</div>
{% endif %}
{% endif %}

fields.custom_source:
//== Media queries breakpoints
//
//## Define the breakpoints at which your layout will change, adapting to different screen sizes.

// Extra small screen / phone
//** Deprecated `$screen-xs` as of v3.0.1
$screen-xs:                  480px !default;
//** Deprecated `$screen-xs-min` as of v3.2.0
$screen-xs-min:              $screen-xs !default;
//** Deprecated `$screen-phone` as of v3.0.1
$screen-phone:               $screen-xs-min !default;

// Small screen / tablet
//** Deprecated `$screen-sm` as of v3.0.1
$screen-sm:                  768px !default;
$screen-sm-min:              $screen-sm !default;
//** Deprecated `$screen-tablet` as of v3.0.1
$screen-tablet:              $screen-sm-min !default;

// Medium screen / desktop
//** Deprecated `$screen-md` as of v3.0.1
$screen-md:                  992px !default;
$screen-md-min:              $screen-md !default;
//** Deprecated `$screen-desktop` as of v3.0.1
$screen-desktop:             $screen-md-min !default;

// Large screen / wide desktop
//** Deprecated `$screen-lg` as of v3.0.1
$screen-lg:                  1200px !default;
$screen-lg-min:              $screen-lg !default;
//** Deprecated `$screen-lg-desktop` as of v3.0.1
$screen-lg-desktop:          $screen-lg-min !default;

// So media queries don't overlap when required, provide a maximum
$screen-xs-max:              ($screen-sm-min - 1) !default;
$screen-sm-max:              ($screen-md-min - 1) !default;
$screen-md-max:              ($screen-lg-min - 1) !default;


//== Grid system
//
//## Define your custom responsive grid.

//** Number of columns in the grid.
$grid-columns:              12 !default;
//** Padding between columns. Gets divided in half for the left and right.
$grid-gutter-width:         30px !default;
// Navbar collapse
//** Point at which the navbar becomes uncollapsed.
$grid-float-breakpoint:     $screen-sm-min !default;
//** Point at which the navbar begins collapsing.
$grid-float-breakpoint-max: ($grid-float-breakpoint - 1) !default;


//== Container sizes
//
//## Define the maximum width of `.container` for different screen sizes.

// Small screen / tablet
$container-tablet:             (720px + $grid-gutter-width) !default;
//** For `$screen-sm-min` and up.
$container-sm:                 $container-tablet !default;

// Medium screen / desktop
$container-desktop:            (940px + $grid-gutter-width) !default;
//** For `$screen-md-min` and up.
$container-md:                 $container-desktop !default;

// Large screen / wide desktop
$container-large-desktop:      (1140px + $grid-gutter-width) !default;
//** For `$screen-lg-min` and up.
$container-lg:                 $container-large-desktop !default;



$font_size_base : 16px;
$font_size_h1 : 2em;
$font_size_h2 : 1.8em;
$font_size_h3 : 1.6em;
$font_size_h4 : 1.4em;
$font_size_h5 : 1.2em;
$font_size_h6 : 1em;

$white: #fff;
$black: #000;

$neutral: #f2f0e9;


$highlight: #941825;

$darkest_grey: #1a1a1a;
$dark_grey: #33312d;
$brown_grey: #545149;
$med_grey: #999999;
$light_grey: #b2b2b1;
$lighter_grey: #e5e3dc;

@mixin clearfix() {
    &:before,
    &:after {
        content: "";
        display: table;
    }
    &:after {
        clear: both;
    }
}
@mixin text-selection{
  ::selection {
    background: $highlight;
    color:$neutral;
  }
  ::-moz-selection {
    background: $highlight;
    color:$neutral;
  }
}
@mixin font-icon(){
  font-family: 'FontAwesome';

  speak: none;
  font-style: normal;
  font-weight: normal;
  font-variant: normal;
  text-transform: none;
  line-height: 1;

  /* Better Font Rendering =========== */
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}
@mixin pointy-hand(){
  cursor: pointer;
  cursor: hand;
}
@mixin opacity($opacity) {
  opacity: $opacity;
  $opacity-ie: $opacity * 100;
  filter: alpha(opacity=$opacity-ie); //IE8
}

@mixin demargin-sides(){
    width: calc( 100% + 30px );
    position: relative;
    left: -15px;
}
@mixin demargin-container(){
    padding-top:1em;
    padding-bottom:1em;
    & > *:first-child{
        margin-top:0;
    }
    & > *:last-child{
        margin-bottom:0;
    }
}

body, html {
  padding:0;
  margin: 0;
  font-size: $font_size_base;
  @include font-feature-settings("kern", "liga", "pnum");
  -webkit-font-smoothing: antialiased;
}

* {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}


@include text-selection;

@import "bootstrap/bootstrap/mixins/grid-framework";
@import "bootstrap/bootstrap/mixins/grid";
@import "bootstrap/bootstrap/grid";


/*
I don't understand the whole margin -15px on .rows,
and I don't like it, so I'm removing it.
Feel to let me know if I'm being a bozo -nina@ninalp.com
*/
.row {
  margin-left: 0px; 
  margin-right: 0px; 
}

body{
    
    color:$dark_grey;
    
    
}
h1, h2, h3, h4, h5, h6{
    
    letter-spacing:1px;
    text-transform:uppercase;
    margin: 1em 0;
}
h1{
    font-size: 2.2em;
}
h2{
    color:$highlight;
    font-size: 1.8em;
}
h3{
    color:$med_grey;
    font-size: 1.5em;
}
h4{
    font-size: 1.17em;
}
h5{
    color:$highlight;
    font-size: 1.17em;
}
h6{
    color:$dark_grey;
    font-size: $font_size_base;
}
a{
    @include transition(all 250ms ease-in-out);
    color:$highlight;
    text-decoration: none;
    
    &:visited{
        color:darken($highlight, 10%);
    }
    &:hover, &:active{
        color:$med_grey;
    }
    &.active{
        font-weight:bold;
    }
    
    &.button{
        display:block;
        padding: 1em 1em 0.65em 1em;
        background-color:$neutral;
        color:$brown_grey;
        text-transform:uppercase;
        font-size:$font_size_h4;
        font-weight:300;
        
        &:hover, &:active{
            background-color:$lighter_grey;
        }
    }
}

ul.nav{
    @include clearfix;
    ul{
        padding:0;
    }
    li{
        float: left;
        list-style-type: none;
        margin: 0 0.5em;
    }
        
    a{
        text-transform: uppercase;
        text-decoration: none;
        color:$dark_grey;
        font-weight: 300;
        &:visited{
            color:$dark_grey;
        }
        &:hover, &:active{
            color:$highlight;
        }
        &.active{
            font-weight:bold;
        }
    }
}
ul, ol{
    li{
        margin-bottom: 0.5em;
    }
}
.inverse{
    background-color:$dark_grey;
    color:$neutral;
    
    a{
        color:$white;
        &:visited{
            color:$white;
        }
        &:hover, &:active{
            color:$highlight;
        }
    }
}

#main{
    & > .container{
        //border: 1px solid transparent;
        
        .inner-container {
            position:relative;
        }
    }
    
    

}

/* helpful tools */
.center-outer {
    display: table;
    position: static;
    width: 100%;
    height: 100%;
}
.center-middle {
    display: table-cell;
    vertical-align: middle;
    width: 100%;
    position: static;
    height: auto !important;
    float: none !important;
}
.center-inner {
    width: 100%;
    height: auto !important;
    float: none !important;
    text-align:center;
    
}
.bodytext{
    figure{
        position:relative;
        
        figcaption{
            width: 100%;
            position:absolute;
            bottom:0;
            padding: 0.5em 1em 0.25em 1em;
            background: #fff;
            background: rgba(255, 255, 255, 0.8);
            font-size:0.8em;
            font-size:0.8rem;
            text-align: left !important;
        }
    }
    ul{
        padding-left:1em;
    }
}
figure.featured{
    margin:0;
    width: calc( 100% + 4em );
    margin-left:-2em;
    figcaption{
        font-size: 0.8em;
        padding: 1em;
    }
    img{
        width:100%;
        display:block;
    }
    
}

.pagination{
    @include clearfix;
    text-align:center;
    
    li{
        display:inline-block;
        list-style-type: none;
        margin: 0 0.5em;
        
        a{
            display:block;
            padding: 1em 1.2em 0.7em;
            color:$white;
            background: $highlight;
            font-weight:bold;
            
            &:hover, &:active, &.current{
                background:$white;
                color: $highlight;
            }
        }
    }
}

.debug-container{
    padding: 0.5em;
}
@mixin debug-item-hover(){
  position:relative;
  &:before{
    content: 'EDIT';
    position:absolute;
    top:0px;
    right:0px;
    font-size: 11px;
    padding: 0.4em 1em 0.2em;
    background: $highlight;
    color:#fff;
    z-index: 1000;
  } 
}
.debug-item{
  &.debug-active{
    border:1px dashed #ccc !important;
    cursor: pointer;
    cursor: hand;

    &:hover{
      border:1px solid $highlight !important;
      @include debug-item-hover();     
    }    
  }
}

#header{
    background-color:$neutral;
    
    
    @include clearfix;
    
    a.navbar-brand{
        
    }
    
    .nav{
        float:right;
    }
    
    
}

@media (max-width: $screen-xs){
    #header{
        .nav{
            padding:0;
            
            li{
                display: block;
                float: none;
                padding:0.5em;
            }
        }
    }
}

#footer{
    //box-shadow: inset 0px 5px 20px 0px rgba(50, 50, 50, 0.1);
    background-color:$white;
    @include opacity(0);
    @include clearfix;
    @include transition(opacity 250ms ease-in-out);
    padding:1em 0;
    
    &.sticky-footer-inited{
        @include opacity(1);
    }
    
    .footer-social-links{
        float:right;    
        text-align: right;
        font-size: 1.4em;
    }
    .footer-links{
        float:left;
    }
    
}

* { background: transparent !important; color: black !important; text-shadow: none !important; filter:none !important; -ms-filter: none !important; } /* Black prints faster: h5bp.com/s */
a, a:visited { text-decoration: underline; }
a[href]:after { content: " (" attr(href) ")"; }
abbr[title]:after { content: " (" attr(title) ")"; }
.ir a:after, a[href^="javascript:"]:after, a[href^="#"]:after { content: ""; }  /* Don't show links for images, or javascript/internal links */
pre, blockquote { border: 1px solid #999; page-break-inside: avoid; }
thead { display: table-header-group; } /* h5bp.com/t */
tr, img { page-break-inside: avoid; }
img { max-width: 100% !important; }
@page { margin: 0.5cm; }
p, h2, h3 { orphans: 3; widows: 3; }
h2, h3 { page-break-after: avoid; }

.debug-container, #header, #footer{
    display:none;
}

/*!
 *  Font Awesome 4.3.0 by @davegandy - http://fontawesome.io - @fontawesome
 *  License - http://fontawesome.io/license (Font: SIL OFL 1.1, CSS: MIT License)
 */
/* FONT PATH
