/* --- SOURCE: discipline-report-bootstrap --- */

function initDisciplineReport(){
    /* Houston, we have javascript */
    $('html').addClass('js');
    
    try {
        var is_lte_IE9 = $(".lt-ie9").length > 0 || $(".ie9").length > 0;
        if(is_lte_IE9){
            $('input, textarea').placeholder();
        }
    }catch(e) {console.log("Error in IE placeholder")}   


    try {
        $(document).debugItem({ 
            itemSelector:'.debug-item',
            itemClassPrefix:'debug-item-',
            toggleSelector:'.debug-item-toggle'
        });        
    }catch(e) { console.log("Error in debug item")}  
    
   
    //STICKY FOOTER
    try {
        $(document).stickyFooter();       
    }catch(e) { console.log("Error in sticky footer")}  
    
    
    try {
        $(document).linkManager();    
    }catch(e) { console.log("Error in link manager")}
    
    
    try{
        $('.mobile-menu-trigger').bind("click", function(event){
            event.preventDefault();
            $('.inner-main-menu').toggleClass("open");
        });
    }catch(e){ console.log("Error in mobile menu trigger") }
    
    
    
}

$(document).ready(function() {
    initDisciplineReport();
});
